<div id="footer" class="footer"> 	<p>cms管理1.0</p>	<p class="copyright">		个人cms管理系统.editor:ryanni			</p></div><script type="text/javascript">	<!-- 全局输入检测-->	var doc = document,		inputs = doc.getElementsByTagName('input'),		supportPlaceholder = 'placeholder' in doc.createElement('input'),		placeholder = function(input){			var text = input.getAttribute('placeholder'),				defaultValue = input.defaultValue;			input.value = text;			input.onfocus = function(){				if(input.value === defaultValue || input.value === text){					this.value = '';				}			}			input.onblur = function(){				if(input.value === ''){					this.value = text;				}			}		};	if(!supportPlaceholder){		for(var i = 0, len = inputs.length; i < len; i++){			var input = inputs[i], text = input.getAttribute('placeholder');			if(input.type === 'text' && text){				placeholder(input);			}		}    	}	<!-- 全局检测结束--></script></body></html>